import { useState } from 'react';

const RickSearch = () => {
    const [name, setName] = useState('');
    const [status, setStatus] = useState('');
    const [species, setSpecies] = useState('');
    const [nextPage, setNextPage] = useState('');
    const [prevPage, setPrevPage] = useState('');
    const [loading, setLoading] = useState(false);
    const [data, setData] = useState(null);
    const [error, setError] = useState(null);

    const url = async (name, status, species, nextPage, prevPage) => {
        setLoading(true);
        setError(null);
        setData(null);
        try {
            const response = await fetch(
                `https://rickandmortyapi.com/api/character/?name=${name}&status=${status}&species=${species}&page=${nextPage}&page=${prevPage}`
            );
            const data = await response.json();
            setData(data);
            setLoading(false);
        } catch (error) {
            setError(error);
            setLoading(false);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Vaciamos el array de personajes.
        setData(null);

        // Establecemos el valor "loading" a "true".
        setLoading(true);

        // Vaciamos el error.
        setError(null);

        try {
            // Llamamos a la función url.
            const response = await url(
                name,
                status,
                species,
                nextPage,
                prevPage
            );
            console.log(response);

            // Si todo ha ido bien almacenamos el array de personajes en "data".
            if (response.ok) {
                setData(data);
            }

            // Este "else" es necesario dado que si no obtenemos ningún personaje
            // con los filtros establecidos el servidor retornará un status 404.
            // Esto no es considerado un error por lo que no entraríamos en el
            // "catch".
            else {
                setError(data?.error || 'Error desconocido');
            }
        } catch (err) {
            setError(err.message || 'Error desconocido');
        } finally {
            setLoading(false);
        }
    };

    return (
        <main>
            <form onSubmit={handleSubmit}>
                <label htmlFor="name">Nombre:</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    onChange={(e) => setName(e.target.value)}
                />

                <label htmlFor="status">Estado:</label>
                <select
                    id="status"
                    name="status"
                    onChange={(e) => setStatus(e.target.value)}
                >
                    <option value="">Cualquiera</option>
                    <option value="alive">Vivo</option>
                    <option value="dead">Muerto</option>
                    <option value="unknown">Desconocido</option>
                </select>

                <label htmlFor="species">Especie:</label>
                <input
                    type="text"
                    id="species"
                    name="species"
                    onChange={(e) => setSpecies(e.target.value)}
                />

                <button disabled={loading}>
                    {loading ? 'Cargando...' : 'Cargar'}
                </button>
            </form>

            {error && <p className="error">{error}</p>}

            {data && (
                <>
                    <p className="data">
                        Se han encontrado {data.info.count} resultados:
                    </p>
                    <ul>
                        {data.results.map((char) => {
                            return (
                                <li key={char.id}>
                                    <img src={char.image} alt="Character" />
                                    <p>{char.name}</p>
                                </li>
                            );
                        })}
                        <button
                            disabled={prevPage === null}
                            onClick={() => setName(prevPage)}
                        ></button>
                        <button
                            disabled={nextPage === null}
                            onClick={() => setName(nextPage)}
                        ></button>
                    </ul>
                </>
            )}
        </main>
    );
};

export default RickSearch;
