import { useState } from 'react';

const RickSearch = () => {
    const [name, setName] = useState('');
    const [status, setStatus] = useState('');
    const [species, setSpecies] = useState('');
    const [loading, setLoading] = useState(false);
    const [nextPage, setNextPage] = useState(null);
    const [prevPage, setPrevPage] = useState(null);
    const [data, setData] = useState(null);
    const [error, setError] = useState(null);

    const fetchData = async () => {
        const response = await fetch(
            `https://rickandmortyapi.com/api/character?name=${name}&status=${status}&species=${species}`
        );
        const data = await response.json();
        setData(data);
        setLoading(false);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Vaciamos el array de personajes.
        setData(null);

        // Establecemos el valor "loading" a "true".
        setLoading(true);

        // Vaciamos el error.
        setError(null);

        try {
            await fetchData();
        } catch (err) {
            setError(err.message || 'Error desconocido');
        } finally {
            setLoading(false);
        }
    };

    const pageNext = async () => {
        const response = await fetch(nextPage);
        const data = await response.json();
        setData(data);
        setLoading(false);
    };

    const pagePrev = async () => {
        const response = await fetch(prevPage);
        const data = await response.json();
        setData(data);
        setLoading(false);
    };

    return (
        <main>
            <form onSubmit={handleSubmit}>
                <label htmlFor="name">Nombre:</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    onChange={(e) => setName(e.target.value)}
                />

                <label htmlFor="status">Estado:</label>
                <select
                    id="status"
                    name="status"
                    onChange={(e) => setStatus(e.target.value)}
                >
                    <option value="">Cualquiera</option>
                    <option value="alive">Vivo</option>
                    <option value="dead">Muerto</option>
                    <option value="unknown">Desconocido</option>
                </select>

                <label htmlFor="species">Especie:</label>
                <input
                    type="text"
                    id="species"
                    name="species"
                    onChange={(e) => setSpecies(e.target.value)}
                />

                <button disabled={loading}>
                    {loading ? 'Cargando...' : 'Cargar'}
                </button>
            </form>

            {error && <p className="error">{error}</p>}

            {data && (
                <>
                    <p className="data">
                        Se han encontrado {data.info.count} resultados:
                    </p>
                    <ul>
                        {data.results.map((char) => {
                            return (
                                <li key={char.id}>
                                    <img src={char.image} alt="Character" />
                                    <p>{char.name}</p>
                                </li>
                            );
                        })}
                        <button onClick={pageNext}>Página siguiente</button>
                        <button onClick={pagePrev}>Página anterior</button>
                    </ul>
                </>
            )}
        </main>
    );
};

export default RickSearch;
